import React, { Component } from "react";
import $ from "jquery";
import { findDOMNode } from "react-dom";

export class Contact extends Component {
  handleToggle = () => {
    const el = findDOMNode(this.refs.toggle);
    $(el).slideToggle();
  };

  render() {
    return (
      <div className="bamb">
        <header ref="toggle" className="zhopa">
          <h1>ZHOPA</h1>
        </header>
        <p className="intro">To get started - zhopa</p>
        <button onClick={this.handleToggle}>Hide Bruce Banner</button>
      </div>
    );
  }
}
